Set-ItemProperty -Path "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Policies\System" -Name "legalnoticecaption" -Value "WARNING: SYSTEM RESTRICTED ACCESS"
Set-ItemProperty -Path "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Policies\System" -Name "legalnoticetext" -Value "Unauthorized access to this system is prohibited. All activities on this system are logged and monitored. By accessing this system, you agree to abide by the company's IT policies and guidelines. Violators will face disciplinary action, up to and including legal consequences. If you are not authorized to access this system, disconnect immediately."